          "social_media",
          "webinars"
        ],
        "content_types": [
          "advanced_tutorials",
          "beta_invitations",
          "case_studies"
        ]
      },
      "at_risk": {
        "characteristics": [
          "declining_engagement",
          "support_tickets",
          "no_recent_activity"
        ],
        "messaging_focus": "retention, support, value_reinforcement",
        "recommended_channels": [
          "email",
          "phone",
          "personalized_outreach"
        ],
        "content_types": [
          "help_resources",
          "success_stories",
          "special_offers"
        ]
      }
    },
    "performance_benchmarks": {
      "email_marketing": {
        "open_rate": {
          "excellent": ">25%",
